/*
 * @lc app=leetcode.cn id=467 lang=golang
 *
 * [467] 环绕字符串中唯一的子字符串
 */

// @lc code=start
func findSubstringInWraproundString(s string) int {
	words := "abcdefghijklmnopqrstuvwxyz"
	wrapRoundMaps := make(map[byte]byte, 26)
	for i := 0; i < 26; i++ {
		wrapRoundMaps[words[i]] = words[(i+1)%26]
	}

	wordMaps := make(map[byte]int, 0)
	collectWordMaxLength := func(start, end int) {
		for i := start; i <= end && i <= start+25; i++ {
			current := wordMaps[s[i]]
			if current >= end-i+1 {
				return
			}
			wordMaps[s[i]] = end - i + 1
		}
	}

	start, end := 0, 0
	for end < len(s) {
		if end+1 >= len(s) {
			collectWordMaxLength(start, end)
			break
		}

		if wrapRoundMaps[s[end]] != s[end+1] {
			collectWordMaxLength(start, end)
			start, end = end+1, end+1
		} else {
			end += 1
		}
	}

	ans := 0
	for _, length := range wordMaps {
		ans += length
	}
	return ans
}

// @lc code=end

func Test467() {
	fmt.Println(findSubstringInWraproundString("") == 259259)
	fmt.Println(findSubstringInWraproundString("cac") == 2)
	fmt.Println(findSubstringInWraproundString("a") == 1)
	fmt.Println(findSubstringInWraproundString("zab") == 6)
}
